	var feedId;
	var feedName;
	var feedUrl;
	var feedFilter;
	var feedBacklog;
	var feedPauseNzb;
	var feedCategory;
	var feedPriority;
	var feedInterval;
	var feedScript;
	var cacheTimeSec;
	var cacheId;
	var updating;
	var updateTimerIntitialized = false;
	var autoUpdate = false;
	var splitRatio;
	var firstUpdate;
